<aside class="control-sidebar bg-light shadow js-control-sidebar">
  <div class="control-toggle js-control-toggle">
    <i class="fas fa-cog"></i>
  </div>
  <div class="control-sidebar__content p-4">
    <h3 class="control-sidebar__title">Style Switcher</h3>
    <div class="style-switcher-item">
      <h4 class="mb-2">Theme Colors</h4>
      <div class="theme-colors js-theme-colors">
        <button
          type="button"
          data-js-theme-color="color-1"
          class="js-theme-color-item color-1"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-2"
          class="js-theme-color-item color-2"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-3"
          class="js-theme-color-item color-3"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-4"
          class="js-theme-color-item color-4"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-5"
          class="js-theme-color-item color-5"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-6"
          class="js-theme-color-item color-6"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-7"
          class="js-theme-color-item color-7"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-8"
          class="js-theme-color-item color-8"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-9"
          class="js-theme-color-item color-9"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-10"
          class="js-theme-color-item color-10"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-11"
          class="js-theme-color-item color-11"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-12"
          class="js-theme-color-item color-12"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-13"
          class="js-theme-color-item color-13"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-14"
          class="js-theme-color-item color-14"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-15"
          class="js-theme-color-item color-15"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-16"
          class="js-theme-color-item color-16"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-17"
          class="js-theme-color-item color-17"
        ></button>
        <button
          type="button"
          data-js-theme-color="color-18"
          class="js-theme-color-item color-18"
        ></button>
      </div>
    </div>

    <h4>Mode Change</h4>
    <div class="mb-4">
      <div class="style-switcher-item">
        <div class="form-check form-switch">
          <input
            class="form-check-input js-dark-mode"
            type="checkbox"
            id="dark-mode"
          />
          <label class="form-check-label" for="dark-mode"
            >Dark Mode</label
          >
        </div>
      </div>
      <div class="style-switcher-item">
        <div class="form-check form-switch">
          <input
            class="form-check-input js-glass-mode"
            type="checkbox"
            id="glass-mode"
          />
          <label class="form-check-label" for="glass-mode"
            >Glass Mode</label
          >
        </div>
      </div>
      <div class="style-switcher-item">
        <div class="form-check form-switch">
          <input
            class="form-check-input js-neumo-mode"
            type="checkbox"
            id="neumo-mode"
          />
          <label class="form-check-label" for="neumo-mode"
            >Neumo Mode</label
          >
        </div>
      </div>
      <div class="style-switcher-item">
        <div class="form-check form-switch">
          <input
            class="form-check-input js-neumo-mode"
            type="checkbox"
            id="claymo-mode"
          />
          <label class="form-check-label" for="claymo-mode"
            >Claymo Mode</label
          >
        </div>
      </div>
    </div>
  </div>
</aside>